mapper.select:
SELECT

FROM wx_store a

WHERE a.id = #{id}

SELECT

FROM wx_store a


    a.del_flag = #{DEL_FLAG_NORMAL}

        AND a.name LIKE
        '%'||#{name}||'%'
        '%'+#{name}+'%'
        concat('%',#{name},'%')


        AND a.user_id = #{userId}


        AND a.id = #{id}




        ORDER BY ${page.orderBy}

SELECT

FROM wx_store a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}

mapper.update:
UPDATE wx_store SET     
    name = #{name},
    user_id = #{userId}
WHERE id = #{id}

UPDATE wx_store SET 
    del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
